"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import { useState } from "react"

import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Checkbox } from "@/components/ui/checkbox"
import {
  Field,
  FieldDescription,
  FieldError,
  FieldGroup,
  FieldLabel,
  FieldSeparator
} from "@/components/ui/field"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Spinner } from "@/components/ui/spinner"
import { useAuth } from "@/hooks/auth/use-auth"
import { useSignInEmail } from "@/hooks/auth/use-sign-in-email"
import { useSignInSocial } from "@/hooks/auth/use-sign-in-social"
import { cn } from "@/lib/utils"
import { MagicLinkButton } from "./magic-link-button"
import { ProviderButtons, type SocialLayout } from "./provider-buttons"

export type SignInProps = AnyAuthConfig & {
  className?: string
  socialLayout?: SocialLayout
  socialPosition?: "top" | "bottom"
}

/**
 * Renders the Sign In UI with email/password, magic link, and social provider
 * options based on the provided `AuthConfig`.
 */
export function SignIn({
  className,
  socialLayout,
  socialPosition = "bottom",
  ...config
}: SignInProps) {
  const context = useAuth(config)

  const {
    basePaths,
    emailAndPassword,
    localization,
    magicLink,
    socialProviders,
    viewPaths,
    Link
  } = context

  const [{ email, password }, signInEmail, signInPending] =
    useSignInEmail(context)

  const [_, signInSocial, socialPending] = useSignInSocial(context)

  const isPending = signInPending || socialPending

  const [fieldErrors, setFieldErrors] = useState<{
    email?: string
    password?: string
  }>({})

  const showSeparator =
    emailAndPassword?.enabled && socialProviders && socialProviders.length > 0

  return (
    <Card className={cn("w-full max-w-sm py-4 md:py-6", className)}>
      <CardHeader className="px-4 md:px-6 -mb-4">
        <CardTitle className="text-xl">{localization.auth.signIn}</CardTitle>
      </CardHeader>

      <CardContent className="px-4 md:px-6">
        <FieldGroup className="gap-4">
          {socialPosition === "top" && (
            <>
              {socialProviders && socialProviders.length > 0 && (
                <ProviderButtons
                  {...config}
                  socialLayout={socialLayout}
                  signInSocial={signInSocial}
                  isPending={isPending}
                />
              )}

              {showSeparator && (
                <FieldSeparator className="*:data-[slot=field-separator-content]:bg-card m-0 text-xs flex items-center">
                  {localization.auth.or}
                </FieldSeparator>
              )}
            </>
          )}

          {emailAndPassword?.enabled && (
            <form action={signInEmail}>
              <FieldGroup className="gap-4">
                <Field className="gap-1">
                  <FieldLabel htmlFor="email">
                    {localization.auth.email}
                  </FieldLabel>

                  <Input
                    id="email"
                    name="email"
                    type="email"
                    autoComplete="email"
                    defaultValue={email}
                    placeholder={localization.auth.emailPlaceholder}
                    required
                    disabled={isPending}
                    onChange={() => {
                      setFieldErrors((prev) => ({
                        ...prev,
                        email: undefined
                      }))
                    }}
                    onInvalid={(e) => {
                      e.preventDefault()
                      setFieldErrors((prev) => ({
                        ...prev,
                        email: (e.target as HTMLInputElement).validationMessage
                      }))
                    }}
                    aria-invalid={!!fieldErrors.email}
                  />

                  <FieldError>{fieldErrors.email}</FieldError>
                </Field>

                <Field className="gap-1">
                  <div className="flex items-center">
                    <FieldLabel htmlFor="password">
                      {localization.auth.password}
                    </FieldLabel>

                    {!emailAndPassword?.rememberMe &&
                      emailAndPassword?.forgotPassword && (
                        <Link
                          href={`${basePaths.auth}/${viewPaths.auth.forgotPassword}`}
                          className="ml-auto inline-block text-sm underline-offset-4 hover:underline text-card-foreground!"
                        >
                          {localization.auth.forgotPasswordLink}
                        </Link>
                      )}
                  </div>

                  <Input
                    id="password"
                    name="password"
                    type="password"
                    autoComplete="current-password"
                    defaultValue={password}
                    placeholder={localization.auth.passwordPlaceholder}
                    required
                    minLength={emailAndPassword?.minPasswordLength}
                    maxLength={emailAndPassword?.maxPasswordLength}
                    disabled={isPending}
                    onChange={() => {
                      setFieldErrors((prev) => ({
                        ...prev,
                        password: undefined
                      }))
                    }}
                    onInvalid={(e) => {
                      e.preventDefault()

                      setFieldErrors((prev) => ({
                        ...prev,
                        password: (e.target as HTMLInputElement)
                          .validationMessage
                      }))
                    }}
                    aria-invalid={!!fieldErrors.password}
                  />

                  <FieldError>{fieldErrors.password}</FieldError>
                </Field>

                {emailAndPassword.rememberMe && (
                  <Field className="my-1">
                    <div className="flex items-center">
                      <div className="flex items-center gap-2">
                        <Checkbox
                          id="rememberMe"
                          name="rememberMe"
                          disabled={isPending}
                        />

                        <Label
                          htmlFor="rememberMe"
                          className="cursor-pointer text-sm font-normal"
                        >
                          {localization.auth.rememberMe}
                        </Label>
                      </div>

                      {emailAndPassword?.forgotPassword && (
                        <Link
                          href={`${basePaths.auth}/${viewPaths.auth.forgotPassword}`}
                          className="ml-auto inline-block text-sm underline-offset-4 hover:underline text-card-foreground!"
                        >
                          {localization.auth.forgotPasswordLink}
                        </Link>
                      )}
                    </div>
                  </Field>
                )}

                <Field className="mt-1">
                  <Button type="submit" disabled={isPending}>
                    {isPending && <Spinner />}

                    {localization.auth.signIn}
                  </Button>

                  {magicLink && (
                    <MagicLinkButton
                      {...config}
                      view="signIn"
                      isPending={isPending}
                    />
                  )}
                </Field>
              </FieldGroup>
            </form>
          )}

          {socialPosition === "bottom" && (
            <>
              {showSeparator && (
                <FieldSeparator className="*:data-[slot=field-separator-content]:bg-card m-0 text-xs flex items-center">
                  {localization.auth.or}
                </FieldSeparator>
              )}

              {socialProviders && socialProviders.length > 0 && (
                <ProviderButtons
                  {...config}
                  socialLayout={socialLayout}
                  signInSocial={signInSocial}
                  isPending={isPending}
                />
              )}
            </>
          )}

          {emailAndPassword?.enabled && (
            <FieldDescription className="flex justify-center gap-1">
              {localization.auth.needToCreateAnAccount}

              <Link
                href={`${basePaths.auth}/${viewPaths.auth.signUp}`}
                className="underline underline-offset-4"
              >
                {localization.auth.signUp}
              </Link>
            </FieldDescription>
          )}
        </FieldGroup>
      </CardContent>
    </Card>
  )
}

"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import type { AuthView } from "@better-auth-ui/react/core"
import { Lock, Mail } from "lucide-react"

import { Button } from "@/components/ui/button"
import { useAuth } from "@/hooks/auth/use-auth"

export type MagicLinkButtonProps = AnyAuthConfig & {
  isPending: boolean
  view?: AuthView
}

/**
 * A button that links to either the magic-link or sign-in route and displays the corresponding icon and label.
 *
 * @param isPending - If true, apply disabled styling and prevent interactions
 * @param view - Current auth view; when `"magicLink"`, the button shows the password/sign-in variant
 */
export function MagicLinkButton({
  isPending,
  view,
  ...config
}: MagicLinkButtonProps) {
  const { basePaths, viewPaths, localization, Link } = useAuth(config)

  const isMagicLinkView = view === "magicLink"

  return (
    <Button
      type="button"
      variant="outline"
      className="w-full"
      disabled={isPending}
      asChild
    >
      <Link
        href={`${basePaths.auth}/${isMagicLinkView ? viewPaths.auth.signIn : viewPaths.auth.magicLink}`}
      >
        {isMagicLinkView ? <Lock /> : <Mail />}

        {localization.auth.continueWith.replace(
          "{{provider}}",
          isMagicLinkView
            ? localization.auth.password
            : localization.auth.magicLink
        )}
      </Link>
    </Button>
  )
}

"use client"

import type { AnyAuthConfig } from "@better-auth-ui/react"
import { providerIcons } from "@better-auth-ui/react"
import { getProviderName } from "@better-auth-ui/react/core"
import { useMemo } from "react"

import { Button } from "@/components/ui/button"
import { Field } from "@/components/ui/field"
import { useAuth } from "@/hooks/auth/use-auth"
import { cn } from "@/lib/utils"

export type ProviderButtonsProps = AnyAuthConfig & {
  isPending: boolean
  socialLayout?: SocialLayout
  signInSocial: (formData: FormData) => void
}

export type SocialLayout = "auto" | "horizontal" | "vertical" | "grid"

/**
 * Render social provider sign-in buttons and handle sign-in initiation and pending state.
 *
 * @param isPending - When true, disables all provider buttons.
 * @param socialLayout - Preferred layout for the provider buttons; when set to `"auto"` the layout is chosen based on the number of available providers.
 * @param signInSocial - The function to call when a social provider button is clicked.
 */
export function ProviderButtons({
  isPending,
  socialLayout = "auto",
  signInSocial,
  ...config
}: ProviderButtonsProps) {
  const { localization, socialProviders } = useAuth(config)

  const resolvedSocialLayout = useMemo(() => {
    if (socialLayout === "auto") {
      if (socialProviders?.length && socialProviders.length >= 4) {
        return "horizontal"
      }

      return "vertical"
    }

    return socialLayout
  }, [socialLayout, socialProviders?.length])

  return (
    <form action={signInSocial}>
      <Field
        className={cn(
          "gap-3",
          resolvedSocialLayout === "grid" && "grid grid-cols-2",
          resolvedSocialLayout === "vertical" && "flex-col",
          resolvedSocialLayout === "horizontal" && "flex-row flex-wrap"
        )}
      >
        {socialProviders?.map((provider) => {
          const ProviderIcon = providerIcons[provider]

          return (
            <Button
              key={provider}
              name="provider"
              value={provider}
              className={cn(
                "flex-1",
                resolvedSocialLayout === "horizontal" && "flex-1"
              )}
              variant="outline"
              type="submit"
              disabled={isPending}
            >
              <ProviderIcon />

              {resolvedSocialLayout === "vertical"
                ? localization.auth.continueWith.replace(
                    "{{provider}}",
                    getProviderName(provider)
                  )
                : resolvedSocialLayout === "grid"
                  ? getProviderName(provider)
                  : null}
            </Button>
          )
        })}
      </Field>
    </form>
  )
}

import {
  type AnyAuthConfig,
  type AuthConfig,
  AuthContext
} from "@better-auth-ui/react"
import { deepmerge, defaultConfig } from "@better-auth-ui/react/core"
import { useContext } from "react"
import { toast } from "sonner"

const extendConfig: AnyAuthConfig = {
  toast,
  Link: (props) => <a {...props} />
}

/**
 * Constructs the effective AuthConfig by merging defaults, any AuthContext-provided config, and the optional `config` overrides.
 *
 * @param config - Partial configuration overrides applied on top of context and defaults
 * @returns The final `AuthConfig` with a non-optional `authClient`
 * @throws If the resulting config does not include an `authClient`
 */
export function useAuth(config?: AnyAuthConfig) {
  const context = useContext(AuthContext)

  const authConfig = deepmerge(
    deepmerge(defaultConfig as AnyAuthConfig, extendConfig),
    deepmerge(context || {}, config || {})
  ) as AuthConfig

  if (authConfig.authClient === undefined) {
    throw new Error("[Better Auth UI] authClient is required")
  }

  return authConfig
}

import type { AnyAuthClient, AnyAuthConfig } from "@better-auth-ui/react"
import { useEffect } from "react"

import { useAuth } from "./use-auth"

/**
 * Redirects unauthenticated users to the sign-in page (preserving the current URL) and exposes the active auth session.
 *
 * @param config - Optional partial AuthConfig used to customize auth behavior; may include an `authClient` override.
 * @returns Result of useSession hook from the auth client
 */
export function useAuthenticate<TAuthClient extends AnyAuthClient>(
  config?: AnyAuthConfig & { authClient?: TAuthClient }
) {
  const { authClient, basePaths, viewPaths, replace } = useAuth(config)
  const { data, isPending, ...rest } = (
    authClient as unknown as TAuthClient
  ).useSession()

  useEffect(() => {
    if (data || isPending) return

    const currentURL = window.location.pathname + window.location.search
    const redirectTo = encodeURIComponent(currentURL)
    const signInPath = `${basePaths.auth}/${viewPaths.auth.signIn}?redirectTo=${redirectTo}`

    replace(signInPath)
  }, [basePaths.auth, data, isPending, viewPaths.auth.signIn, replace])

  return { data, isPending, ...rest }
}

import { type AnyAuthConfig, useRedirectTo } from "@better-auth-ui/react"
import { useActionState } from "react"

import { useAuth } from "./use-auth"

export function useSignInEmail(config?: AnyAuthConfig) {
  const {
    authClient,
    baseURL,
    emailAndPassword,
    localization,
    toast,
    navigate
  } = useAuth(config)

  const { refetch } = authClient.useSession()
  const redirectTo = useRedirectTo(config)

  const signInEmail = async (_: object, formData: FormData) => {
    const email = formData.get("email") as string
    const password = formData.get("password") as string
    const rememberMe = formData.get("rememberMe") === "on"

    const { error } = await authClient.signIn.email(
      {
        email,
        password,
        ...(emailAndPassword?.rememberMe ? { rememberMe } : {})
      },
      { disableSignal: true }
    )

    if (error) {
      if (error.code === "EMAIL_NOT_VERIFIED") {
        const toastId = toast.error(error.message || error.statusText, {
          action: {
            label: localization.auth.resend,
            onClick: async () => {
              const callbackURL = `${baseURL}${redirectTo}`

              toast.dismiss?.(toastId)

              const { error } = await authClient.sendVerificationEmail({
                email,
                callbackURL
              })

              if (error) {
                toast.error(error.message || error.statusText)
              } else {
                toast.success(localization.auth.verificationEmailSent)
              }
            }
          }
        })
      } else {
        toast.error(error.message || error.statusText)
      }

      return {
        email,
        password: ""
      }
    }

    await refetch()

    navigate(redirectTo)

    return { email, password }
  }

  return useActionState(signInEmail, {
    email: "",
    password: ""
  })
}

import { type AnyAuthConfig, useRedirectTo } from "@better-auth-ui/react"
import { useActionState } from "react"

import { useAuth } from "./use-auth"

export function useSignInSocial(config?: AnyAuthConfig) {
  const { authClient, baseURL, toast } = useAuth(config)
  const redirectTo = useRedirectTo(config)

  const signInSocial = async (_: object, formData: FormData) => {
    const provider = formData.get("provider") as string

    const callbackURL = `${baseURL}${redirectTo}`

    const { error } = await authClient.signIn.social({
      provider,
      callbackURL
    })

    if (error) toast.error(error.message || error.statusText)

    return { provider }
  }

  return useActionState(signInSocial, { provider: "" })
}
